mapper.select:
SELECT * FROM coupons
WHERE c_code = #{c_code}
AND u_id = #{u_id}

mapper.update:
UPDATE coupons
SET u_id = #{u_id}, c_code = #{c_code}, is_coupon_used = #{is_coupon_used},
    c_start = #{c_start}, c_end = #{c_end}, c_discount_rate = #{c_discount_rate}
WHERE c_code = #{c_code}

UPDATE coupons
SET is_coupon_used = 1
WHERE c_code = #{c_code}
